mapper.select:
select * from (select ROWNUM as rn, a.* from ( SELECT c.lt_cnq_num, c.ttl, c.cntn, c.drwup_dt, c.hit, m.name, atch_num
FROM lt_cnq c JOIN mem m on c.mem_num = m.mem_num WHERE LT_NUM = #{vo.ltNum}
AND TTL like '%' || #{vo.ttl} || '%'
AND CNTN like '%' || #{vo.cntn} || '%'
AND NAME like '%' || #{vo.name} || '%'
ORDER by LT_CNQ_NUM DESC
) a where ROWNUM <= #{svo.end}) b where rn >= #{svo.start}

mapper.insert:

	SELECT 'cn' || LPAD(NVL(MAX(SUBSTR(LT_CNQ_NUM, -5)), 0) +
	1, 5, '0') AS LT_CNQ_NUM
	FROM lt_cnq

INSERT INTO lt_Cnq (
			LT_CNQ_NUM
			, TTL
			, CNTN
			, DRWUP_DT
			, MEM_NUM
			, LT_NUM
			, HIT
			, ATCH_NUM
			)
VALUES (
			#{ltCnqNum}
			, #{ttl}
			, #{cntn}
			, sysDate
			, #{memNum}
			, #{ltNum}
			, 0
			,#{atchNum}
		)

mapper.update:
UPDATE lt_cnq
SET    TTL = #{ttl},
		CNTN = #{cntn}
WHERE LT_CNQ_NUM = #{ltCnqNum}

UPDATE lt_cnq 
	SET hit = hit + 1 
WHERE lt_cnq_num = #{ltCnqNum}
