mapper.insert:


	SELECT 'at' || LPAD(NVL(MAX(SUBSTR(atch_num, -5)), 0) + 1, 5, '0') 
	FROM atch

INSERT INTO atch
				(
                atch_num

                , atch_path		                

                , atch_ttl
                , atch_typ_cd
                , atch_mark_ttl
                )
	VALUES 
			(
			#{atchNum}

			, #{atchPath}

			, #{atchTtl}
			, #{atchTypCd}
			, #{atchMarkTtl}
			)
