mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, a.* FROM (
SELECT l.lt_num , find_code(l.lt_st_cd) lt_st_cd , l.lt_ttl , l.lt_img , l.lt_intro , find_code(l.lt_cate_cd) lt_cate_cd , l.fr_dt , find_code(l.accp_yn_cd) accp_yn_cd , find_code(l.lt_dif_cd) lt_dif_cd , l.lecturer_num , find_code(l.lt_keyword_cd) lt_keyword_cd , find_code(l.rdm_cate_cd) rdm_cate_cd , lec.name FROM lt l JOIN lecturer lec ON l.lecturer_num = lec.lecturer_num
ORDER BY lt_num DESC
) a WHERE ROWNUM <= #{pvo.last} ) b WHERE rn >= #{pvo.first}

mapper.insert:

	SELECT 'lt' || LPAD(NVL(MAX(SUBSTR(LT_NUM, -5)), 0) + 1,
	5, '0') AS LT_NUM
	FROM LT

INSERT INTO LT(
LT_NUM
,LT_TTL
,LT_INTRO
,FR_DT
,LT_IMG
,LECTURER_NUM
,lt_keyword_cd
,lt_dif_cd
,lt_cate_cd
,ACCP_YN_CD
,RDM_CATE_CD

)
VALUES(
#{ltNum}
,#{ltTtl}
,#{ltIntro}
,#{frDt}
,#{ltImg}
,#{lecturerNum}
,#{ltKeywordCd}
,#{ltDifCd}
,#{ltCateCd}
,'d2'
,#{rdmCateCd}

)

mapper.update:
UPDATE lt
SET ACCP_YN_CD = #{accpYnCd}
WHERE lt_num = #{ltNum}
