mapper.insert:


	SELECT 'no' || LPAD(NVL(MAX(SUBSTR(NOTE_NUM, -5)), 0) + 1, 5, '0') AS NOTE_NUM 
	FROM NOTE

INSERT INTO NOTE(NOTE_NUM
				, NOTE_TTL
				, NOTE_CNTN
				, SENT_DT
				, REC_DT
				, SENT_PS
				, REC_PS
				)
VALUES ( 
		  #{noteNum}
		, #{noteTtl}
		, #{noteCntn}
		, sysdate
		, sysdate
		, #{sentPs}
		, #{recPs}

		)
