mapper.insert:

	SELECT 'st' || LPAD(NVL(MAX(SUBSTR(sttl_num, -5)), 0) + 1, 5, '0') 
	FROM sttl

INSERT INTO sttl (
					sttl_num
					, sttl_typ_cd
					, sttl_dt
					, sttl_st_cd

					, dc_price

					, all_sttl_price
					, subsp_num
					, mem_num
					, sttl_accp_num
					, sttl_rnd
	                )
	VALUES (
			#{sttlNum}
	        , #{sttlTypCd}
	        , #{sttlDt}
	        , #{sttlStCd}

	        , #{dcPrice}			        

	        , #{allSttlPrice}
	        , #{subspNum}
	        , #{memNum}
	        , #{sttlAccpNum}
	        , #{sttlRnd}
	        )
