mapper.insert:

	SELECT 'su' || LPAD(NVL(MAX(SUBSTR(subsp_num, -5)), 0) + 1, 5, '0') AS SUBSP_NUM 
	FROM subsp

INSERT INTO subsp (
					subsp_num
	                , subsp_st_cd
	                , subsp_fr_dt
	                , subsp_to_dt
	                , subsp_typ_cd
	                , subsp_price
	                , mem_num
	                )
	VALUES (
			#{subspNum}
	        , #{subspStCd}
	        , SYSDATE
	        , SYSDATE + 30
	        , #{subspTypCd}
	        , #{subspPrice}
	        , #{memNum}
	        )

mapper.update:
UPDATE subsp
SET billing_key = #{billingKey}
WHERE subsp_num = #{subspNum}
