mapper.insert:


	SELECT 'pr' || LPAD(NVL(MAX(SUBSTR(PRBL_NUM, -5)), 0) + 1, 5, '0') AS PRBL_NUM 
	FROM PRBL

INSERT INTO PRBL(PRBL_NUM
                 , PRBL_CNTN
                 , QUES1
                 , QUES2
                 , QUES3
                 , QUES4
                 , ANS
                 , CTY
                 , LT_NUM)
VALUES ( 
		  #{prblNum}
		, #{prblCntn}
		, #{ques1}
		, #{ques2}
	    , #{ques3}
		, #{ques4}
		, #{ans}
		, #{cty}
		, #{ltNum}
		)

mapper.update:
		UPDATE PRBL
		SET PRBL_CNTN = #{prblCntn}
            , QUES1 = #{ques1}
            , QUES2 = #{ques2}
            , QUES3 = #{ques3}
            , QUES4 = #{ques4}
            , ANS = #{ans}
            , CTY = #{cty}
		WHERE PRBL_NUM = #{prblNum}

mapper.insert:

	SELECT 'ap' || LPAD(NVL(MAX(SUBSTR(APPLEXAM_NUM, -5)), 0) + 1, 5, '0') AS APPLEXAM_NUM
	FROM APPLEXAM

INSERT INTO APPLEXAM (APPLEXAM_NUM, MEM_NUM, LT_NUM)
VALUES (
	#{applexamNum}
	, #{memNum}
	, #{ltNum}
)

mapper.delete:
DELETE FROM APPLEXAM
WHERE MEM_NUM = #{memNum}
AND LT_NUM = #{ltNum}

mapper.update:
UPDATE APPLEXAM_DETAIL
SET ANSWER = #{answer}
WHERE PRBL_NUM = #{prblNum}
AND APPLEXAM_NUM = #{applexamNum}

UPDATE APPLEXAM
SET find_code(PASS_YN_CD) PASS_YN_CD = #{passYnCd}
	, SCORE = #{score}
	, APPLEXAM_DT = sysdate
WHERE APPLEXAM_NUM = #{applexamNum}
